import React, { useState, useCallback, useMemo } from 'react';

const pathogenicPattern = /(-VAR_)|(-[A-Z]\d+[A-Z])/;
const associationPattern = /\| Association:(.*?)(?=\s*\||$)/;
const clinicalSignificancePattern = /\| ClinicalSignificance:(.*?)(?=\s*\||$)/;

const App = () => {
  const [sampleInputs, setSampleInputs] = useState([{ id: 1, name: '', sourceSoftware: 'Peaks Studio', files: { peptides: null, proteins: null } }]);
  const [fastaFile, setFastaFile] = useState(null);
  const [fastaIds, setFastaIds] = useState(null);
  const [processedData, setProcessedData] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterPathogenic, setFilterPathogenic] = useState(false);
  const [minTotalPeptides, setMinTotalPeptides] = useState('');
  const [minArea, setMinArea] = useState('');
  const [minUniquePeptides, setMinUniquePeptides] = useState('');
  const [manualFastaIdsText, setManualFastaIdsText] = useState('');
  const [expandedRow, setExpandedRow] = useState(null);

  const handleFileChange = (e, id, fileType) => {
    const file = e.target.files[0];
    setSampleInputs(prevInputs => prevInputs.map(input =>
      input.id === id ? { ...input, files: { ...input.files, [fileType]: file } } : input
    ));
  };

  const handleNameChange = (e, id) => {
    const { value } = e.target;
    setSampleInputs(prevInputs => prevInputs.map(input =>
      input.id === id ? { ...input, name: value } : input
    ));
  };

  const addSampleInput = () => {
    const newId = sampleInputs.length ? Math.max(...sampleInputs.map(s => s.id)) + 1 : 1;
    setSampleInputs([...sampleInputs, { id: newId, name: '', sourceSoftware: 'Peaks Studio', files: { peptides: null, proteins: null } }]);
  };

  const handleRemoveInput = (id) => {
    if (sampleInputs.length > 1) {
      setSampleInputs(sampleInputs.filter(input => input.id !== id));
    }
  };

  const processData = useCallback(async () => {
    setError(null);
    setLoading(true);
    setProcessedData([]);
    setFastaIds(null);

    try {
      let parsedFastaIds = null;
      if (fastaFile) {
        const fastaText = await fastaFile.text();
        const ids = new Set();
        const lines = fastaText.split('\n');
        for (const line of lines) {
          if (line.startsWith('>')) {
            const parts = line.substring(1).split('|');
            if (parts.length > 1) {
              ids.add(parts[1].trim());
            } else {
              ids.add(parts[0].trim());
            }
          }
        }
        parsedFastaIds = ids;
      }

      const parsedManualIds = new Set();
      if (manualFastaIdsText.trim()) {
        const idsArray = manualFastaIdsText.split(/[\s,;]+/).map(id => id.trim()).filter(id => id.length > 0);
        idsArray.forEach(id => parsedManualIds.add(id));
      }

      let combinedFastaIds = null;
      if (parsedFastaIds || parsedManualIds.size > 0) {
        combinedFastaIds = new Set();
        if (parsedFastaIds) {
          parsedFastaIds.forEach(id => combinedFastaIds.add(id));
        }
        if (parsedManualIds.size > 0) {
          parsedManualIds.forEach(id => combinedFastaIds.add(id));
        }
      }
      setFastaIds(combinedFastaIds);
      
      const allProteins = [];
      const allPeptides = new Map();
      const groupCounts = new Map();

      for (const sample of sampleInputs) {
        if (!sample.name || !sample.files.peptides || !sample.files.proteins) {
          setError(`Falta información para la muestra ${sample.id}. Asegúrese de que el nombre y ambos archivos están cargados.`);
          setLoading(false);
          return;
        }

        const peptidesText = await sample.files.peptides.text();
        const proteinsText = await sample.files.proteins.text();

        const peptidesRows = peptidesText.split('\n').slice(1).filter(row => row.trim() !== '');
        const proteinsRows = proteinsText.split('\n').slice(1).filter(row => row.trim() !== '');

        peptidesRows.forEach(row => {
          const columns = row.split('\t');
          const peptideSequence = columns[3] || 'N/A';
          const proteinAccessionFull = columns[2] || '';
          const proteinAccession = proteinAccessionFull.split('|').length > 1 ? proteinAccessionFull.split('|')[1] : proteinAccessionFull;

          if (proteinAccession) {
            const peptideId = `${sample.name}-${proteinAccession}`;
            if (!allPeptides.has(peptideId)) {
                allPeptides.set(peptideId, new Set());
            }
            allPeptides.get(peptideId).add(peptideSequence);
          }
        });

        const proteins = proteinsRows.map(row => {
          const columns = row.split('\t');
          const accessionFull = columns[2] || '';
          const accession = accessionFull.split('|').length > 1 ? accessionFull.split('|')[1] : accessionFull;
          const description = columns[columns.length - 1].replace(/"/g, '').trim();
          const proteinGroup = columns[0] || 'N/A';
          const area = parseFloat(columns[6]) || 0;
          const totalPeptides = parseInt(columns[7], 10) || 0;
          
          let diseaseAssociation = 'N/A';
          
          const associationMatch = description.match(associationPattern);
          const clinicalSignificanceMatch = description.match(clinicalSignificancePattern);

          const associations = [];
          if (associationMatch && associationMatch[1]) {
              associations.push(associationMatch[1].trim());
          }
          if (clinicalSignificanceMatch && clinicalSignificanceMatch[1]) {
              associations.push(clinicalSignificanceMatch[1].trim());
          }
          
          if (associations.length > 0) {
              diseaseAssociation = associations.join('; ');
          } else {
              if (description.match(pathogenicPattern)) {
                  diseaseAssociation = 'Patogénico/Variante';
              }
              if (description.toLowerCase().includes('cancer')) {
                  diseaseAssociation = 'Cáncer';
              }
          }

          const uniquePeptidesForProtein = Array.from(allPeptides.get(`${sample.name}-${accession}`) || []);
          const uniquePeptidesCount = uniquePeptidesForProtein.length;

          return {
            accession,
            description,
            proteinGroup,
            area,
            totalPeptides,
            uniquePeptidesCount,
            uniquePeptides: uniquePeptidesForProtein,
            sampleName: sample.name,
            diseaseAssociation
          };
        });

        allProteins.push(...proteins);
      }

      allProteins.forEach(protein => {
          groupCounts.set(protein.proteinGroup, (groupCounts.get(protein.proteinGroup) || 0) + 1);
      });

      const finalProteins = allProteins.map(protein => ({
          ...protein,
          isUnitaryGroup: groupCounts.get(protein.proteinGroup) === 1
      }));

      setProcessedData(finalProteins);

    } catch (err) {
      console.error('Error procesando los archivos:', err);
      setError(`Error procesando los archivos. Por favor, asegúrese de que el formato es correcto. Detalles: ${err.message}`);
    } finally {
      setLoading(false);
    }
  }, [sampleInputs, fastaFile, manualFastaIdsText]);

  const filteredData = useMemo(() => {
    let currentData = processedData;

    if (fastaIds && fastaIds.size > 0) {
        currentData = currentData.filter(protein => {
          const baseAccession = protein.accession.split('-')[0];
          return fastaIds.has(baseAccession);
        });
    }
    
    if (filterPathogenic) {
        currentData = currentData.filter(protein => protein.diseaseAssociation.includes('Patogénico/Variante') || protein.diseaseAssociation.includes('PATHOGENIC_VARIANT'));
    }

    const parsedMinTotalPeptides = parseInt(minTotalPeptides, 10);
    if (!isNaN(parsedMinTotalPeptides)) {
        currentData = currentData.filter(protein => protein.totalPeptides >= parsedMinTotalPeptides);
    }
    
    const parsedMinArea = parseFloat(minArea);
    if (!isNaN(parsedMinArea)) {
        currentData = currentData.filter(protein => protein.area >= parsedMinArea);
    }
    
    const parsedMinUniquePeptides = parseInt(minUniquePeptides, 10);
    if (!isNaN(parsedMinUniquePeptides)) {
        currentData = currentData.filter(protein => protein.uniquePeptidesCount >= parsedMinUniquePeptides);
    }

    return currentData.filter(protein =>
      protein.accession.toLowerCase().includes(searchTerm.toLowerCase()) ||
      protein.description.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [processedData, fastaIds, searchTerm, filterPathogenic, minTotalPeptides, minArea, minUniquePeptides]);

  const toggleRow = (index) => {
    setExpandedRow(expandedRow === index ? null : index);
  };
  
  return (
    <div className="container">
      <header className="header">
        <h1>Análisis de Biomarcadores</h1>
        <p>Unifica, analiza y visualiza datos de proteínas y péptidos.</p>
      </header>

      <div className="section">
        <h2>Paso 1: Opcional - Filtrar por IDs de Proteínas</h2>
        <div>
          <label>Sube tu archivo FASTA:</label>
          <input type="file" accept=".fasta,.txt" onChange={(e) => setFastaFile(e.target.files[0])} />
          {fastaFile && <p>Archivo seleccionado: {fastaFile.name}</p>}
        </div>
        <div style={{ marginTop: '10px' }}>
          <label>O introduce IDs manualmente (separados por coma o espacio):</label>
          <textarea rows="4" value={manualFastaIdsText} onChange={(e) => setManualFastaIdsText(e.target.value)} placeholder="Ej: P01234, Q56789"></textarea>
        </div>
      </div>

      <div className="section">
        <h2>Paso 2: Cargar Archivos de Muestras</h2>
        <div className="file-input-group">
          {sampleInputs.map((sample) => (
            <div key={sample.id} style={{ border: '1px solid #eee', padding: '10px', borderRadius: '4px', position: 'relative' }}>
              <input type="text" value={sample.name} onChange={(e) => handleNameChange(e, sample.id)} placeholder={`Nombre de Muestra ${sample.id}`} style={{ width: 'calc(100% - 20px)' }} />
              <input type="file" accept=".txt" onChange={(e) => handleFileChange(e, sample.id, 'peptides')} />
              <small>Archivo de Péptidos: {sample.files.peptides?.name || 'No seleccionado'}</small>
              <input type="file" accept=".txt" onChange={(e) => handleFileChange(e, sample.id, 'proteins')} />
              <small>Archivo de Proteínas: {sample.files.proteins?.name || 'No seleccionado'}</small>
              {sampleInputs.length > 1 && (
                <button onClick={() => handleRemoveInput(sample.id)} style={{ position: 'absolute', top: '5px', right: '5px', background: 'none', border: 'none', color: 'red', cursor: 'pointer' }}>X</button>
              )}
            </div>
          ))}
          <div style={{ display: 'flex', gap: '10px' }}>
              <button className="btn btn-secondary" onClick={addSampleInput}>Añadir Otra Muestra</button>
              <button className="btn btn-primary" onClick={processData} disabled={loading}>{loading ? 'Procesando...' : 'Analizar Archivos'}</button>
          </div>
        </div>
      </div>

      {error && <div className="error">{error}</div>}

      {processedData.length > 0 && (
        <div className="section">
          <h2>Paso 3: Resultados y Filtros</h2>
          <div style={{ marginBottom: '10px' }}>
              <input type="text" placeholder="Buscar..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
              <label style={{ marginLeft: '10px' }}>
                  <input type="checkbox" checked={filterPathogenic} onChange={(e) => setFilterPathogenic(e.target.checked)} />
                  Mostrar solo Patogénicos
              </label>
          </div>
          <div style={{ marginBottom: '10px', display: 'flex', gap: '10px' }}>
              <input type="number" placeholder="Mín. Péptidos Totales" value={minTotalPeptides} onChange={(e) => setMinTotalPeptides(e.target.value)} />
              <input type="number" step="0.01" placeholder="Mín. Área" value={minArea} onChange={(e) => setMinArea(e.target.value)} />
              <input type="number" placeholder="Mín. Péptidos Únicos" value={minUniquePeptides} onChange={(e) => setMinUniquePeptides(e.target.value)} />
          </div>
          <table className="results-table">
            <thead>
              <tr>
                <th>ID de Acceso</th>
                <th>Descripción</th>
                <th>Muestra</th>
                <th>Asociación</th>
                <th>Grupo Unitario</th>
                <th>Área</th>
                <th>Péptidos Totales</th>
                <th>Péptidos Únicos</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {filteredData.map((protein, index) => (
                <React.Fragment key={index}>
                  <tr>
                    <td>{protein.accession}</td>
                    <td>{protein.description}</td>
                    <td>{protein.sampleName}</td>
                    <td>{protein.diseaseAssociation}</td>
                    <td>{protein.isUnitaryGroup ? 'Sí' : 'No'}</td>
                    <td>{protein.area.toFixed(2)}</td>
                    <td>{protein.totalPeptides}</td>
                    <td>{protein.uniquePeptidesCount}</td>
                    <td>
                      <button onClick={() => toggleRow(index)}>
                        {expandedRow === index ? 'Cerrar' : 'Ver Péptidos'}
                      </button>
                    </td>
                  </tr>
                  {expandedRow === index && (
                    <tr>
                      <td colSpan="9">
                        <h4 style={{ fontWeight: 'bold' }}>Péptidos Únicos:</h4>
                        <ul className="peptide-list">
                          {protein.uniquePeptides.length > 0 ? (
                            protein.uniquePeptides.map((peptide, pIndex) => (
                              <li key={pIndex}>{peptide}</li>
                            ))
                          ) : (
                            <li>No hay péptidos únicos asociados.</li>
                          )}
                        </ul>
                      </td>
                    </tr>
                  )}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};
        
export default App;
